mapper.select:
select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE FROM ( select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from (select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_CTRCT_INF_DEL union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_CTRCT_INF_DEL_HIST union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_ACCT_INF_DEL union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_ACCT_INF_DEL_HIST union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_SEC_ACCT_DEL union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_SEC_ACCT_DEL_HIST union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_CTRCT_INF_ENT_DEL union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_CTRCT_INF_ENT_DEL_HIST union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_SEC_ACCT_ENT_DEL union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_SEC_ACCT_ENT_DEL_HIST union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_ACCT_INF_ENT_DEL union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_ACCT_INF_ENT_DEL_HIST union all select ID_NUM, ID_TYPE, COMPANY, null as DEL_REC_CODE from E_BS_INF_DLT union all select ID_NUM, ID_TYPE, COMPANY, null as DEL_REC_CODE from E_BS_INF_DLT_HIST union all select ID_NUM, ID_TYPE, COMPANY, null as DEL_REC_CODE from E_BALANCE_SHEET_DLT union all select ID_NUM, ID_TYPE, COMPANY, null as DEL_REC_CODE from E_BALANCE_SHEET_DLT_HIST union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_IN_COME_ENT_DEL union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_IN_COME_ENT_DEL_HIST union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_CASH_FLOW_ENT_DEL union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_CASH_FLOW_ENT_DEL_HIST union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_ASSETS_DEBT_ENT_DEL union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_ASSETS_DEBT_ENT_DEL_HIST union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_IN_OUT_ENT_DEL union all select ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE from E_IN_OUT_ENT_DEL_HIST) a where 1=1
and a.ID_NUM like '%${pd.idNum}%'
and a.ID_TYPE = #{pd.idType}
and a.DEL_REC_CODE = #{pd.delRecCode}
and a.company = #{pd.COMPANY} group by ID_NUM, ID_TYPE, COMPANY, DEL_REC_CODE ) ORDER BY ID_NUM, ID_TYPE

mapper.delete:
delete from report_message
 where REPORT_MESSAGE_FILE_NAME = #{reportMessageId}
   and company = #{COMPANY}

