INSERT INTO validate_template VALUES ('gr_jd_is_one14', null, '“账户状态”和“当前还款状态”需要保持一致：“账户状态”为“3-关闭”/“4-销户”时，“当前还款状态”应为“C-结清”/“C-销户”、“G-结束', '“账户状态”和“当前还款状态”需要保持一致：“账户状态”为“3-关闭”/“4-销户”时，“当前还款状态”应为“C-结清”/“C-销户”、“G-结束', null, 'SELECT #table_pk#,\'\'business_pk, #master_table_id# as master_table_id FROM #table_name# WHERE #table_field# IN(#field1#) AND #table_field2# NOT IN(#field2#) [and #validate_date# = \'#currentDate#\'] {and #table_pk#=\'#tablePk#\'}', 'analyticValidateServiceImpl_sql', null);
