INSERT INTO validate_template VALUES ('gr_R2321209_01', null, '更正在保责任信息段时，若待入库的更正请求记录中的信息报告日期晚于库中最新的在保责任信息段的信息报告日期，且库中在保责任信息段中的账户状态为“关闭”,则该更正请求记录不能入库', '更正在保责任信息段时，若待入库的更正请求记录中的信息报告日期晚于库中最新的在保责任信息段的信息报告日期，且库中在保责任信息段中的账户状态为“关闭”,则该更正请求记录不能入库', null, 'SELECT #table_pk#,\'\'business_pk, A.#master_table_id# as master_table_id FROM #table_name#  A WHERE #table_field#>(SELECT MAX(A.RPT_DATE) FROM #table_name#_HIST A LEFT JOIN I_GR_REPYMT_INF_HIST B ON A.#table_pk#=B.#table_pk# WHERE  ACCT_STATUS=\'2\' AND INF_REC_TYPE=\'230\') and ACCT_CODE IN (SELECT ACCT_CODE FROM I_GUAR_ACCT_BS A LEFT JOIN I_GR_REPYMT_INF B ON A.PDA_GUAR_ACCT_BS_SGMT_ID=B.PDA_GUAR_ACCT_BS_SGMT_ID WHERE  ACCT_STATUS=\'2\' AND INF_REC_TYPE=\'230\') [and #validate_date# = \'#currentDate#\'] {and A.#table_pk#=\'#tablePk#\'}', 'analyticValidateServiceImpl_sql', null);
