INSERT INTO validate_template VALUES ('gr_jd_is_one10', null, '若“当前还款状态”为“N”或“M”，则“当前逾期期数”、“当前逾期总额”必须为0（若此账户没有上述数据项，则不校验此数据项）', '若“当前还款状态”为“N”或“M”，则“当前逾期期数”、“当前逾期总额”必须为0（若此账户没有上述数据项，则不校验此数据项）', null, 'SELECT #table_pk#,\'\'business_pk, #master_table_id# as master_table_id FROM #table_name# WHERE #table_field# IN(#field1#) AND (#table_field1# !=#field2# OR #table_field2# !=#field3#) [and #validate_date# = \'#currentDate#\'] {and #table_pk#=\'#tablePk#\'}', 'analyticValidateServiceImpl_sql', null);
