INSERT INTO validate_template VALUES ('FIDLD_IS_NULL_OR_NOT_NULL', null, '就业状况为11、13、17、21、24、91时，单位名称、性质、所属行业、详细地址、所在地邮编、所在地行政区划、电话、职业、职务、职称和本单位工作起始年份必须出现', '就业状况为11、13、17、21、24、91时，单位名称、性质、所属行业、详细地址、邮编、所在地行政区划、电话、职业、职务、职称和工作起始年份必须出现；就业状况为上述以外的代码，上述数据项不能出现。', null, 'SELECT A.#table_pk#,\'\'business_pk, A.#master_table_id# as master_table_id FROM #table_name# A WHERE (#table_field# IN (#a_field2#) AND (#table_field_a1# IS NULL OR #table_field_a1# = \'\' OR #table_field_a2# IS NULL OR #table_field_a2# = \'\' OR #table_field_a3# IS NULL OR #table_field_a3# = \'\' OR #table_field_a4# IS NULL OR  #table_field_a4# = \'\' OR #table_field_a5# IS NULL OR #table_field_a5# = \'\' OR #table_field_a6# IS NULL OR #table_field_a6# = \'\' OR #table_field_a7# IS NULL OR #table_field_a7# = \'\' OR #table_field_a8# IS NULL OR #table_field_a8# = \'\' OR #table_field_a9# IS NULL OR #table_field_a9# = \'\' OR #table_field_a10# IS NULL OR #table_field_a10# = \'\' OR #table_field_a11# IS NULL OR #table_field_a11# = \'\')) OR (#table_field# NOT IN (#a_field2#) AND (#table_field_a1# IS NOT NULL AND #table_field_a1# <> \'\' OR #table_field_a2# IS NOT NULL AND #table_field_a2# <> \'\' OR #table_field_a3# IS NOT NULL AND #table_field_a3# <> \'\' OR #table_field_a4# IS NOT NULL AND #table_field_a4# <> \'\' OR #table_field_a5# IS NOT NULL AND #table_field_a5# <> \'\' OR #table_field_a6# IS NOT NULL AND #table_field_a6# <> \'\' OR #table_field_a7# IS NOT NULL AND #table_field_a7# <> \'\' OR #table_field_a8# IS NOT NULL AND #table_field_a8# <> \'\' OR #table_field_a9# IS NOT NULL AND #table_field_a9# <> \'\'  OR #table_field_a10# IS NOT NULL AND #table_field_a10# <> \'\' OR #table_field_a11# IS NOT NULL AND #table_field_a11# <> \'\')) [and #validate_date# = \'#currentDate#\'] {and #table_pk#=\'#tablePk#\'}', 'analyticValidateServiceImpl_sql', null);
INSERT INTO validate_template VALUES ('gr_jd_is_one3', null, '“开户日期”应不晚于“到期日期”', '“开户日期”应不晚于“到期日期”', null, 'SELECT #table_pk#,\'\'business_pk, #master_table_id# as master_table_id FROM #table_name# WHERE #table_field#>#table_field1# and #table_field1# <> \'\' [and #validate_date# = \'#currentDate#\'] {and #table_pk#=\'#tablePk#\'}', 'analyticValidateServiceImpl_sql', null);
INSERT INTO validate_template VALUES ('gr_jd_is_one19', null, '“当前还款状态”只能为“N-正常', '“当前还款状态”只能为“N-正常', null, 'SELECT #table_pk#,\'\'business_pk, a.#master_table_id# as master_table_id FROM #table_name# A left join i_acct_bs b on a.PBA_ACCT_BS_SGMT_ID = b.PBA_ACCT_BS_SGMT_ID WHERE #table_field# != #field1# and b.acct_type <> \'C1\' [and #validate_date# = \'#currentDate#\'] {and #table_pk#=\'#tablePk#\'}', 'analyticValidateServiceImpl_sql', null);
INSERT INTO validate_template VALUES ('CHECK_PBI_NOMONTHACCTSTATUS', null, '对于 C1 账户，更正非月度表现信息段时，若库中存在信息报告日期更晚的非月度表现信息段，则更正请求记录中账户状态不能为“结束”', '对于 C1 账户，更正非月度表现信息段时，若库中存在信息报告日期更晚的非月度表现信息段，则更正请求记录中账户状态不能为“结束”', null, 'SELECT DISTINCT A.#table_pk#, \'\' business_pk, A.#master_table_id# as master_table_id FROM #table_name# A LEFT JOIN #table_1# B ON A.#table_pk_1# = B.#table_pk_1# WHERE (B.#table_field_1# = \'#num1#\' AND B.#table_field_2# < (SELECT MAX(#table_field_2#) FROM #table_1# WHERE #table_field_3# = \'#num2#\' AND #table_pk_1# NOT IN (B.#table_pk_1#)) AND A.#table_field# = \'#num3#\') [AND #validate_date# = \'#currentDate#\'] {AND a.#table_pk#=\'#tablePk#\'}', 'analyticValidateServiceImpl_sql', null);
