mapper.select:
select eja_cash_flows_bs_sgmt_id ,inf_rec_type ,ent_name ,ent_cert_type ,ent_cert_num ,rpt_date ,sheet_year ,sheet_type ,sheet_type_divide ,audit_firm_name ,auditor_name ,audit_time ,cimoc ,rpt_date_code ,last_update_time ,operator_user ,org_name ,org_code ,business_states ,reject_reason ,is_filter ,report_message_id ,COMPANY from E_CASH_FLOWS_BS
LEFT JOIN PECR.sys_l_c_a_m_m M ON E_CASH_FLOWS_BS.ENT_NAME = M.CUST_NAME AND E_CASH_FLOWS_BS.ENT_CERT_TYPE = M.CUST_ID_TYPE AND E_CASH_FLOWS_BS.ENT_CERT_NUM = M.CUST_ID_NUM
and business_states in ('10','11','40','80','110','20')
and business_states in ('20')
and rpt_date = #{pd.rptDate}
and ent_name like '%${pd.entName}%'
and ent_cert_type = #{pd.entCertType}
and ent_cert_num like '%${pd.entCertNum}%'
and business_states = #{pd.businessStates}
AND M.MANAGER_ID = #{pd.managerId} AND M.DATA_TYPE = '2' AND M.DATA_LEVEL = '6' AND M.END_DATE = '2099-12-31'
AND COMPANY = #{pd.COMPANY}

	select count(1) 
	  from E_CASH_FLOWS_BS
     where eja_cash_flows_bs_sgmt_id = #{ejaCashFlowsBsSgmtId}

mapper.delete:
delete from E_CASH_FLOWS_BS
 where eja_cash_flows_bs_sgmt_id = #{ejaCashFlowsBsSgmtId}
   and COMPANY = #{COMPANY}

mapper.select:
     select count(1) 
	  from E_CASH_FLOWS2002
     where eja_cash_flows_bs_sgmt_id = #{ejaCashFlowsBsSgmtId} 
       and ejb_cash_flows2002_sgmt_id = #{ejbCashFlows2002SgmtId}

     select count(1) 
	  from E_CASH_FLOWS2007
     where eja_cash_flows_bs_sgmt_id = #{ejaCashFlowsBsSgmtId} 
       and ejc_cash_flows2007_sgmt_id = #{ejbCashFlows2007SgmtId}
