mapper.select:
select eca_en_icdn_rltp_inf_id ,inf_rec_type ,ent_name ,ent_cert_type ,ent_cert_num ,asso_ent_name ,asso_ent_cert_type ,asso_ent_cert_num ,asso_type ,asso_sign ,last_update_time ,operator_user ,org_name ,org_code ,business_states ,reject_reason ,is_filter ,report_message_id ,rpt_date ,COMPANY from E_ICDN_RLTP_INF
LEFT JOIN PECR.sys_l_c_a_m_m M ON ENT_NAME = M.CUST_NAME AND ENT_CERT_TYPE = M.CUST_ID_TYPE AND ENT_CERT_NUM = M.CUST_ID_NUM
and business_states in ('10','11','40','80','110','20')
and business_states in ('20')
and rpt_date = #{pd.rptDate}
and ent_name like '%${pd.entName}%'
and ent_cert_type = #{pd.entCertType}
and ent_cert_num like '%${pd.entCertNum}%'
and business_states = #{pd.businessStates}
and M.MANAGER_ID = #{pd.managerId} AND M.DATA_TYPE = '2' AND M.DATA_LEVEL = '1' AND M.END_DATE = '2099-12-31'
and COMPANY = #{pd.COMPANY}

mapper.delete:
delete from E_ICDN_RLTP_INF
 where eca_en_icdn_rltp_inf_id = #{ecaEnIcdnRltpInfId}
 and COMPANY = #{COMPANY}
