mapper.select:
	select
		 eea_guar_acct_bs_sgmt_id 
    from E_GUAR_ACCT_BS
    where name = #{pd.name}
      and id_type = #{pd.idType}
      and id_num = #{pd.idNum}
      and acct_type = #{pd.acctType}

      and company = #{pd.COMPANY}

select eea_guar_acct_bs_sgmt_id ,inf_rec_type ,acct_type ,acct_code ,rpt_date ,rpt_date_code ,name ,id_type ,id_num ,mngmt_org_code ,last_update_time ,operator_user ,org_name ,org_code ,business_states ,reject_reason ,is_filter ,report_message_id ,mdfc_sgmt_code ,COMPANY from E_GUAR_ACCT_BS where 1=1 and inf_rec_type = '442'
and id_num like '%${pd.idNum}%'
and name like '%${pd.name}%'
and rpt_Date = #{pd.rptDate}
and acct_type = #{pd.acctType}
and acct_code = #{pd.acctCode}
and business_states = #{pd.businessStates}
and business_states in ('10','11','40','80','110','20')
and business_states in ('20')
and COMPANY = #{pd.COMPANY}

	select count(1) 
	  from E_GUAR_ACCT_BS
     where eea_guar_acct_bs_sgmt_id = #{eeaGuarAcctBsSgmtId}

mapper.delete:
delete from E_GUAR_ACCT_BS
 where eea_guar_acct_bs_sgmt_id = #{eeaGuarAcctBsSgmtId}
   and COMPANY = #{COMPANY}

mapper.select:
	select count(1) 
	  from E_GUAR_ACCT_BS_INF
     where eeb_guar_acct_bs_inf_sgmt_id = #{eebGuarAcctBsInfSgmtId} 
       and eea_guar_acct_bs_sgmt_id = #{eeaGuarAcctBsSgmtId}

mapper.insert:
insert into E_GUAR_ACCT_BS_INF( eeb_guar_acct_bs_inf_sgmt_id, busi_lines, busi_dtil_lines, open_date, guar_amt, cy, due_date, guar_mode, oth_repy_guarway, sec_dep, ctrct_txt_code, eea_guar_acct_bs_sgmt_id, COMPANY) select sys_guid(),
concat (hex (RAND ()), hex (RAND ())),
busi_lines, busi_dtil_lines, open_date, guar_amt, cy, due_date, guar_mode, oth_repy_guarway, sec_dep, ctrct_txt_code, #{eeaGuarAcctBsSgmtId}, company from E_GUAR_ACCT_BS_INF_HIST where eea_guar_acct_bs_sgmt_id = #{oldEeaGuarAcctBsSgmtId}

mapper.delete:
	delete from E_GUAR_ACCT_BS_INF
	 where eeb_guar_acct_bs_inf_sgmt_id = #{eebGuarAcctBsInfSgmtId} 
       and eea_guar_acct_bs_sgmt_id = #{eeaGuarAcctBsSgmtId}

mapper.insert:
insert into E_SEC_RLT_REPYMT_INF( eec_rlt_repymt_inf_sgmt_id, INFO_ID_TYPE, arlp_name, arlp_cert_type, arlp_cert_num, arlp_type, arlp_amt, warty_sign, max_guar_mcc, EEA_GUAR_ACCT_BS_SGMT_ID, COMPANY) select sys_guid(),
concat (hex (RAND ()), hex (RAND ())),
INFO_ID_TYPE, arlp_name, arlp_cert_type, arlp_cert_num, arlp_type, arlp_amt, warty_sign, max_guar_mcc, #{eeaGuarAcctBsSgmtId}, company from E_SEC_RLT_REPYMT_INF_HIST where EEA_GUAR_ACCT_BS_SGMT_ID = #{oldEeaGuarAcctBsSgmtId}

mapper.update:
update E_SEC_RLT_REPYMT_INF
INFO_ID_TYPE = #{infoIdType, jdbcType=VARCHAR}, arlp_name = #{arlpName, jdbcType=VARCHAR}, arlp_cert_type = #{arlpCertType, jdbcType=VARCHAR}, arlp_cert_num = #{arlpCertNum, jdbcType=VARCHAR}, arlp_type = #{arlpType, jdbcType=VARCHAR}, arlp_amt = #{arlpAmt}, warty_sign = #{wartySign, jdbcType=VARCHAR}, max_guar_mcc = #{maxGuarMcc, jdbcType=VARCHAR}
where EEA_GUAR_ACCT_BS_SGMT_ID = #{eeaGuarAcctBsSgmtId} and eec_rlt_repymt_inf_sgmt_id = #{eecRltRepymtInfSgmtId}

mapper.delete:
delete from E_SEC_RLT_REPYMT_INF
 where EEA_GUAR_ACCT_BS_SGMT_ID = #{eeaGuarAcctBsSgmtId} 
   and eec_rlt_repymt_inf_sgmt_id = #{eecRltRepymtInfSgmtId}

mapper.select:
	select count(1) 
	  from E_SEC_RLT_REPYMT_INF
     where EEA_GUAR_ACCT_BS_SGMT_ID = #{eeaGuarAcctBsSgmtId}
       and eec_rlt_repymt_inf_sgmt_id = #{eecRltRepymtInfSgmtId}

	select count(1) 
	  from E_GUAR_MOTGA_CLTAL_INF
     where EEA_GUAR_ACCT_BS_SGMT_ID = #{eeaGuarAcctBsSgmtId}
       and eed_guar_motga_cltal_inf_id = #{eedGuarMotgaCltalInfId}

mapper.insert:
insert into E_GUAR_MOTGA_CLTAL_INF( eed_guar_motga_cltal_inf_id, ccc, eea_guar_acct_bs_sgmt_id, COMPANY) select sys_guid(),
concat (hex (RAND ()), hex (RAND ())),
ccc, #{eeaGuarAcctBsSgmtId}, COMPANY from E_GUAR_MOTGA_CLTAL_INF_HIST where eea_guar_acct_bs_sgmt_id = #{oldEeaGuarAcctBsSgmtId}

mapper.update:
	update E_GUAR_MOTGA_CLTAL_INF


	   	  	ccc = #{ccc, jdbcType=VARCHAR}


	   where eea_guar_acct_bs_sgmt_id = #{eeaGuarAcctBsSgmtId}
         and eed_guar_motga_cltal_inf_id = #{eedGuarMotgaCltalInfId}

mapper.delete:
	delete from E_GUAR_MOTGA_CLTAL_INF
	 where eea_guar_acct_bs_sgmt_id = #{eeaGuarAcctBsSgmtId}
       and eed_guar_motga_cltal_inf_id = #{eedGuarMotgaCltalInfId}

